<?php

namespace EDB\AdminBundle\Twig;

use EDB\AdminBundle\Entity\Media;
use EDB\AdminBundle\Service\ImageServer;
use Twig\Extension\AbstractExtension;
use Twig\TwigFunction;

class MediaExtension extends AbstractExtension
{
    private ImageServer $imageServer;
    private string $mediaPath;
    private string $sourcePrefix;

    public function __construct(ImageServer $imageServer, string $mediaPath, string $sourcePrefix)
    {
        $this->imageServer = $imageServer;
        $this->mediaPath = $mediaPath;
        $this->sourcePrefix = $sourcePrefix;
    }

    public function getFunctions(): array
    {
        return [
            new TwigFunction('media_path', [$this, 'getMediaPath'], ['is_safe' => ['html']]),
            new TwigFunction('render_media', [$this, 'renderMedia'], ['is_safe' => ['html']]),
        ];
    }

    private function isImage(string $mimeType): bool
    {
        return in_array($mimeType, [
            'image/bmp',
            'image/jpeg',
            'image/png',
        ]);
    }

    public function renderMedia(?Media $media, int $width, int $height, string $fit = "crop")
    {
        if (empty($media)) return null;

        if ($this->isImage($media->getMimeType())) {
            $imageUrl = $this->imageServer->makeImage($media->getFilename(), [
                'w' => $width,
                'h' => $height,
                'fit' => $fit
            ]);

            return sprintf(
                '/%s/%s',
                trim($this->mediaPath, '/'),
                $imageUrl
            );
        }

        return $this->getFileUploadThumbnail();
    }

    public function getMediaPath(?Media $media)
    {
        if (empty($media)) return null;

        return sprintf(
            '/%s/%s/%s',
            trim($this->mediaPath, '/'),
            trim($this->sourcePrefix, '/'),
            $media->getFilename()
        );
    }

    private function getFileUploadThumbnail()
    {
        return "
        data:image/png;base64,
        ";
    }
}